			),
			array(
				'title' => __('Most Commented', 'dp'),
				'orderby' => 'comments',
				'view' => 'list-medium'
			)
		);
		$cats = get_terms('category');
		foreach($cats as $cat) {
			$default_home_sections[] = array('taxonomies'=> array('category'=>$cat->term_id), 'title'=>$cat->name);
		}
		
		$fields = array(
			'dp-home-featured-settings' => array( // Home Featured Settings
				array(
