				$result = copy_dir($source, $remote_destination);
				if ( is_wp_error($result) ) {
					if ( $clear_working )
						$wp_filesystem->delete($remote_source, true);
					return $result;
				}
		
				//Clear the Working folder?
				if ( $clear_working )
					$wp_filesystem->delete($remote_source, true);
		
				$destination_name = basename( str_replace($local_destination, '', $destination) );
				if ( '.' == $destination_name )
					$destination_name = '';
		
				$this->result = compact('local_source', 'source', 'source_name', 'source_files', 'destination', 'destination_name', 'local_destination', 'remote_destination', 'clear_destination', 'delete_source_dir');
		
				$res = apply_filters('upgrader_post_install', true, $hook_extra, $this->result);
				if ( is_wp_error($res) ) {
					$this->result = $res;
					return $res;
				}
		
				//Bombard the calling function will all the info which we've just used.
				return $this->result;
			}
		
			function run($options) {
		
				$defaults = array( 	'package' => '', //Please always pass this.
									'destination' => '', //And this
									'clear_destination' => false,
									'clear_working' => true,
									'is_multi' => false,
									'hook_extra' => array() //Pass any extra $hook_extra args here, this will be passed to any hooked filters.
								);
		
				$options = wp_parse_args($options, $defaults);
				extract($options);
		
				//Connect to the Filesystem first.
				$res = $this->fs_connect( array(WP_CONTENT_DIR, $destination) );
				if ( ! $res ) //Mainly for non-connected filesystem.
					return false;
		
				if ( is_wp_error($res) ) {
					$this->skin->error($res);
					return $res;
				}
		
				if ( !$is_multi ) // call $this->header separately if running multiple times
					$this->skin->header();
		
				$this->skin->before();
		
				//Download the package (Note, This just returns the filename of the file if the package is a local file)
				$download = $this->download_package( $package );
				if ( is_wp_error($download) ) {
					$this->skin->error($download);
					$this->skin->after();
					return $download;
				}
		
				//Unzip's the file into a temporary directory
				$working_dir = $this->unpack_package( $download );
				if ( is_wp_error($working_dir) ) {
					$this->skin->error($working_dir);
					$this->skin->after();
					return $working_dir;
				}
		
				//With the given options, this installs it to the destination directory.
				$result = $this->install_package( array(
													'source' => $working_dir,
													'destination' => $destination,
													'clear_destination' => $clear_destination,
													'clear_working' => $clear_working,
													'hook_extra' => $hook_extra
												) );
				$this->skin->set_result($result);
				if ( is_wp_error($result) ) {
					$this->skin->error($result);
					$this->skin->feedback('process_failed');
				} else {
					//Install Suceeded
					$this->skin->feedback('process_success');
				}
				$this->skin->after();
		
				if ( !$is_multi )
					$this->skin->footer();
		
				return $result;
			}
		
			function maintenance_mode($enable = false) {
				global $wp_filesystem;
				$file = $wp_filesystem->abspath() . '.maintenance';
				if ( $enable ) {
					$this->skin->feedback('maintenance_start');
					// Create maintenance file to signal that we are upgrading
					$maintenance_string = '<?php $upgrading = ' . time() . '; ?>';
					$wp_filesystem->delete($file);
					$wp_filesystem->put_contents($file, $maintenance_string, FS_CHMOD_FILE);
				} else if ( !$enable && $wp_filesystem->exists($file) ) {
					$this->skin->feedback('maintenance_end');
					$wp_filesystem->delete($file);
				}
			}
		}
	endif;
?>